export default {
  name: "AddTeacher",
  data() {
    return {
      firstname: '',
      lastname: '',
      email: '',
      phonenumber: ''
    };
  },
  methods: {
    submitForm(e) {
      e.preventDefault();
      if (!this.firstname) {
        alert('Please add the first name');
        return;
      } else if (!this.lastname) {
        alert('Please add the last name');
        return;
      } else if (!this.email) {
        alert('Please add the email address');
        return;
      } else if (!this.phonenumber) {
        alert('Please add the phone number');
        return;
      }
    }
  }
};
